import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Textbox = () => {
  const [language, setLanguage] = useState('C++');
  const [code, setCode] = useState('//Enter your C++ code here');
  const onChange = e => setCode(e.target.value);

  return (
    <div>
      <form className='form'>
        <textarea
          rows='4'
          cols='20'
          name='comment'
          form='usrform'
          className='#ffebee red lighten-5'
          style={{ height: '500px' }}
          value={code}
          onChange={onChange}
        />

        <button
          type='submit'
          value='Run'
          style={{ width: '100px', height: '50px' }}
          className='btn waves-effect waves-light'
        >
          Run
        </button>
      </form>
    </div>
  );
};
Textbox.propTypes = {
  initial_text: PropTypes.string.isRequired
};

export default Textbox;
