import React, { Component } from 'react';
import Navbar from './components/Navbar';
import Textbox from './components/Textbox';

const App = () => {
  const [language, setLanguage] = useState('C++');
  const [
    code,
    setCode
  ] = useState(`#include<iostream>; <br> using namespace std; <br>
  int main() <br>
  { <br>
    cout << "Hello World"; <br>
  }
  `);

  const changeLanguage = lang => {
    setLanguage(lang);
  };
  return (
    <div className='App'>
      <Navbar />

      <Textbox initial_text={code} />
    </div>
  );
};

export default App;
