import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';

const Textbox = () => {
  const [language, setLanguage] = useState('C++');
  const [code, setCode] = useState('//Enter your C++ code here');
  const onChange = e => {
    setCode(e.target.value);
  };

  const getOutput = e => {
    e.preventDefault();
    console.log('Hello, Ritvik this side');
    axios
      .post('https://api.judge0.com/submissions', {
        source_code: `
        #include<iostream>
        using namespace std;
        int main()
        {
            cout << "Hello world";
        }
        `,
        language_id: 15
      })
      .then(f => {
        axios
          .get('https://api.judge0.com/submissions/{f.token}')
          .then(e => {
            console.log(e);
          })
          .catch(error => {
            console.log(error);
          });
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div>
      <form onSubmit={getOutput} className='form'>
        <textarea
          rows='4'
          cols='20'
          name='comment'
          form='usrform'
          placeholder='Enter your code here'
          className='#ffebee red lighten-5'
          style={{ height: '500px' }}
          value={code}
          onChange={onChange}
        />
        <input
          type='submit'
          value='Run'
          className='btn waves-effect waves-light'
        />
      </form>
    </div>
  );
};

export default Textbox;
