import React, { useState } from 'react';
import PropTypes from 'prop-types';

const Textbox = ({ getOutput }) => {
  const [language, setLanguage] = useState('C++');
  const [code, setCode] = useState('//Enter your C++ code here');
  const onChange = e => {
    setCode(e.target.value);
  };
  return (
    <div>
      <form onSubmit={getOutput({ code }, { language })} className='form'>
        <textarea
          rows='4'
          cols='20'
          name='comment'
          form='usrform'
          placeholder='Enter your code here'
          className='#ffebee red lighten-5'
          style={{ height: '500px' }}
          value={code}
          onChange={onChange}
        />
        <input type='submit' value='Run' className='btn btn-dark btn-block' />
      </form>
    </div>
  );
};
Textbox.propTypes = {
  getOutput: PropTypes.func.isRequired
};

export default Textbox;
