import React, { useState } from 'react';
import PropTypes from 'prop-types';
import axios from 'axios';
// import CodeMirror from '@uiw/react-codemirror';
import { Controlled as CodeMirror } from 'react-codemirror2';

// import 'codemirror/addon/display/autorefresh';
// import 'codemirror/addon/comment/comment';
// import 'codemirror/addon/edit/matchbrackets';
// import 'codemirror/keymap/sublime';
// import 'codemirror/theme/dracula.css';

const Textbox = ({ lang, lang_id }) => {
  const [language, setLanguage] = useState(lang);
  const [code, setCode] = useState('//Enter your ' + lang + ' code here');
  const [token, setToken] = useState(null);
  const [output, setOutput] = useState('');
  const [input, setInput] = useState('');
  const [id, setId] = useState(parseInt(lang_id));
  const onChange1 = e => {
    setCode(e.target.value);
  };

  const onChange2 = e => {
    setInput(e.target.value);
  };

  const getOutput = e => {
    e.preventDefault();
    console.log(code);

    axios
      .post(`https://api.judge0.com/submissions`, {
        source_code: code,
        language_id: id,
        stdin: input
      })
      .then(f => {
        console.log(f);
        setToken(f.data.token);
        axios
          .get(`https://api.judge0.com/submissions/${token}`)
          .then(g => {
            console.log(g);
            if (g.data.stdout != null) {
              setOutput(g.data.stdout);
            } else {
              setOutput(g.data.stderr);
            }
          })
          .catch(error => {
            console.log(error);
          });
      })
      .catch(error => {
        console.log(error);
      });
  };

  return (
    <div>
      <form onSubmit={getOutput} className='form'>
        <textarea
          id='id1'
          rows='4'
          cols='20'
          className='#ffe082 amber lighten-4'
          style={{ height: '500px' }}
          value={code}
          onChange={onChange1}
        />
        <CodeMirror
          value={code}
          options={
            {
              // theme: 'dracula',
              // keyMap: 'sublime',
              // mode: 'clojure',
              // lineNumbers: true,
              // rtlMoveVisually: true
            }
          }
          onBeforeChange={(editor, data, value) => {
            setCode(value);
            console.log({ value });
          }}
          onChange={(editor, data, value) => {
            console.log({ value });
          }}
        />
        <textarea
          rows='4'
          cols='20'
          name='comment'
          form='usrform'
          placeholder='Enter your input here'
          className='#ffe082 amber lighten-4'
          style={{ height: '100px' }}
          value={input}
          onChange={onChange2}
        />

        {/* <input
              value={output}
              style={{ overflow: 'scroll', height: '200px' }}
            /> */}
        <textarea
          rows='4'
          cols='20'
          name='comment'
          form='usrform'
          className='#ffe082 amber lighten-4'
          style={{ height: '100px' }}
          value={output}
        />

        <input
          type='submit'
          value='Run'
          className='btn waves-effect waves-light'
        />
      </form>
    </div>
  );
};
Textbox.propTypes = {
  lang: PropTypes.string.isRequired,
  lang_id: PropTypes.number.isRequired
};
export default Textbox;
